import { blackboard } from '../core/Blackboard';

/**
 * ==================================================================================
 * SHAM ARCHITECT SYS - PRO GEN 8.0 (FURNISHED)
 * ==================================================================================
 */

const LOG = (msg) => console.log(`[Architect Core] ${msg}`);

// ==================================================================================
// 1. KNOWLEDGE BASE
// ==================================================================================

const ROOM_SPECS = {
  'living':     { weight: 2.2, minDim: 14, label: 'LIVING' },
  'dining':     { weight: 1.4, minDim: 11, label: 'DINING' },
  'garage':     { weight: 2.0, minDim: 12, label: 'GARAGE' },
  'kitchen':    { weight: 1.5, minDim: 11, label: 'KITCHEN' },
  'master-bed': { weight: 1.8, minDim: 13, label: 'MASTER SUITE' },
  'bedroom':    { weight: 1.3, minDim: 10, label: 'BEDROOM' },
  'study':      { weight: 1.0, minDim: 9,  label: 'STUDY' },
  'library':    { weight: 1.2, minDim: 10, label: 'LIBRARY' },
  'bathroom':   { weight: 0.8, minDim: 6,  label: 'BATH' },
  'utility':    { weight: 0.9, minDim: 7,  label: 'LAUNDRY' },
  'prayer':     { weight: 0.8, minDim: 7,  label: 'PRAYER' },
  'store':      { weight: 0.5, minDim: 5,  label: 'STORE' },
  'hallway':    { weight: 0,   minDim: 4,  label: 'HALL' },
  'stairs':     { weight: 0,   minDim: 8,  label: 'STAIRS' }
};

// ==================================================================================
// 2. GEOMETRY ENGINE
// ==================================================================================

class Rect {
  constructor(x, y, w, h) {
    this.x = x; this.y = y; this.w = w; this.h = h;
  }
  get right() { return this.x + this.w; }
  get bottom() { return this.y + this.h; }
}

// ==================================================================================
// 3. AGENT IMPLEMENTATIONS
// ==================================================================================

// --- AGENT 1: ZONING ---
const ZoningAgent = async (config) => {
  LOG('Zoning rooms...');
  blackboard.write('config', config);

  const pad = 4;
  const buildW = config.width - (pad*2);
  const buildD = config.depth - (pad*2);
  blackboard.write('siteBoundary', new Rect(pad, pad, buildW, buildD));

  const inventory = { ...config.rooms };
  const floorPlans = Array.from({ length: config.floors }, () => ({ clusters: [] }));

  const assign = (fIdx, cluster) => {
    const f = Math.min(fIdx, config.floors - 1);
    floorPlans[f].clusters.push(cluster);
  };

  // 1. Master Suite
  if (inventory.bedroom > 0) {
    const cluster = { type: 'SUITE', main: 'master-bed', subs: [], id: 'master' };
    inventory.bedroom--;
    if (inventory.bathroom > 0) { cluster.subs.push('bathroom'); inventory.bathroom--; }
    assign(config.floors > 1 ? 1 : 0, cluster);
  }

  // 2. Standard Beds
  let curF = config.floors > 1 ? 1 : 0;
  while (inventory.bedroom > 0) {
    const cluster = { type: 'ROOM', main: 'bedroom', subs: [], id: `bed-${inventory.bedroom}` };
    inventory.bedroom--;
    if (inventory.bathroom > 0 && Math.random() > 0.5) {
       cluster.type = 'SUITE'; cluster.subs.push('bathroom'); inventory.bathroom--;
    }
    assign(curF, cluster);
    if (config.floors > 2 && curF < config.floors - 1) curF++;
    else if (config.floors > 1) curF = 1;
  }

  // 3. Ground Core
  assign(0, { type: 'ROOM', main: 'kitchen', subs: [], id: 'kitchen' });
  if (inventory.dining > 0) assign(0, { type: 'ROOM', main: 'dining', subs: [], id: 'dining' });
  assign(0, { type: 'ROOM', main: 'living', subs: [], id: 'living' });
  if (inventory.garage > 0) assign(0, { type: 'ROOM', main: 'garage', subs: [], id: 'garage' });

  // 4. Extras
  ['bathroom', 'utility', 'prayer', 'library', 'study', 'store'].forEach(type => {
    while (inventory[type] > 0) {
      assign(0, { type: 'ROOM', main: type, subs: [], id: `${type}-shared` });
      inventory[type]--;
    }
  });

  blackboard.write('floorPlans', floorPlans);
  return true;
};

// --- AGENT 2: LAYOUT SOLVER ---
const LayoutAgent = async () => {
  LOG('Calculating layout...');
  const site = blackboard.read('siteBoundary');
  const floorPlans = blackboard.read('floorPlans');
  const config = blackboard.read('config');
  const allFloorsRegions = [];

  const SPINE_W = 6;
  const SPINE_X = site.x + (site.w - SPINE_W) / 2;

  floorPlans.forEach((plan) => {
    const regions = [];

    // Spine
    const hallRect = new Rect(SPINE_X, site.y + 6, SPINE_W, site.h - 10);
    regions.push({ ...hallRect, type: 'hallway' });
    if (config.floors > 1) {
       regions.push({ x: SPINE_X, y: hallRect.bottom - 10, w: SPINE_W, h: 10, type: 'stairs' });
       hallRect.h -= 10;
    }

    // Wings
    const leftWing = { x: site.x, y: site.y, w: (site.w - SPINE_W)/2, h: site.h };
    const rightWing = { x: SPINE_X + SPINE_W, y: site.y, w: (site.w - SPINE_W)/2, h: site.h };

    // Sort
    const getScore = (c) => {
       const name = c.main;
       if (name.includes('kitchen') || name.includes('master')) return 0; 
       if (name.includes('dining') || name.includes('utility')) return 20;
       if (name.includes('bed')) return 30;
       if (name.includes('living')) return 80;
       if (name.includes('garage')) return 90; 
       return 50;
    };
    plan.clusters.sort((a,b) => getScore(a) - getScore(b));

    const leftC = [], rightC = [];
    plan.clusters.forEach(c => {
      if (c.main === 'garage' || c.main === 'living') { 
         if(leftC.length <= rightC.length) leftC.push(c); else rightC.push(c);
      }
      else if (c.main === 'kitchen') leftC.push(c); 
      else if (c.main === 'dining') leftC.push(c);
      else {
         if (leftC.length <= rightC.length) leftC.push(c); else rightC.push(c);
      }
    });

    const stack = (wing, clusters, side) => {
       if (clusters.length === 0) return;
       const totalW = clusters.reduce((sum, c) => sum + (ROOM_SPECS[c.main]?.weight || 1), 0);
       let curY = wing.y;

       clusters.forEach(c => {
          const spec = ROOM_SPECS[c.main] || { weight: 1 };
          const h = (spec.weight / totalW) * wing.h;
          const row = new Rect(wing.x, curY, wing.w, h);

          if (c.type === 'SUITE' && c.subs.length > 0) {
             const bathW = row.w * 0.35; const bedW = row.w - bathW;
             if (side === 'LEFT') {
                regions.push({ x: row.x, y: curY, w: bathW, h: h, type: c.subs[0] });
                regions.push({ x: row.x + bathW, y: curY, w: bedW, h: h, type: c.main });
             } else {
                regions.push({ x: row.x, y: curY, w: bedW, h: h, type: c.main });
                regions.push({ x: row.x + bedW, y: curY, w: bathW, h: h, type: c.subs[0] });
             }
          } else {
             regions.push({ ...row, type: c.main });
          }
          curY += h;
       });
    };

    stack(leftWing, leftC, 'LEFT');
    stack(rightWing, rightC, 'RIGHT');
    allFloorsRegions.push(regions);
  });

  blackboard.write('floorRegions', allFloorsRegions);
  return true;
};

// --- AGENT 3: STRUCTURAL ---
const StructuralAgent = async () => {
  LOG('Generating Walls...');
  const floorRegions = blackboard.read('floorRegions');
  const allWalls = [];
  floorRegions.forEach((rooms) => {
    const walls = [];
    rooms.forEach(r => {
      walls.push({ x1: r.x, y1: r.y, x2: r.x+r.w, y2: r.y, type: 'inner' });
      walls.push({ x1: r.x, y1: r.y+r.h, x2: r.x+r.w, y2: r.y+r.h, type: 'inner' });
      walls.push({ x1: r.x, y1: r.y, x2: r.x, y2: r.y+r.h, type: 'inner' });
      walls.push({ x1: r.x+r.w, y1: r.y, x2: r.x+r.w, y2: r.y+r.h, type: 'inner' });
    });
    const b = blackboard.read('siteBoundary');
    const outer = [
      { x1: b.x, y1: b.y, x2: b.x+b.w, y2: b.y },
      { x1: b.x, y1: b.y+b.h, x2: b.x+b.w, y2: b.y+b.h },
      { x1: b.x, y1: b.y, x2: b.x, y2: b.y+b.h },
      { x1: b.x+b.w, y1: b.y, x2: b.x+b.w, y2: b.y+b.h },
    ];
    allWalls.push({ inner: walls, outer: outer });
  });
  blackboard.write('floorWalls', allWalls);
  return true;
};

// --- AGENT 4: CONNECTIVITY ---
const ConnectivityAgent = async () => {
  LOG('Placing Doors...');
  const floorRegions = blackboard.read('floorRegions');
  const allDoors = [];
  floorRegions.forEach((rooms, fIdx) => {
    const doors = [];
    const add = (d) => { if(!doors.some(x=>Math.abs(x.x-d.x)<1 && Math.abs(x.y-d.y)<1)) doors.push(d); };

    rooms.forEach((r1, i) => {
      rooms.forEach((r2, j) => {
        if(i<=j) return;
        const oX = (Math.min(r1.right, r2.right) - Math.max(r1.x, r2.x));
        const oY = (Math.min(r1.bottom, r2.bottom) - Math.max(r1.y, r2.y));
        const tX = Math.abs(r1.right - r2.x) < 0.1 || Math.abs(r2.right - r1.x) < 0.1;
        const tY = Math.abs(r1.bottom - r2.y) < 0.1 || Math.abs(r2.bottom - r1.y) < 0.1;

        const isHall = r1.type === 'hallway' || r2.type === 'hallway';
        const isSuite = (r1.type==='bathroom' && r2.type.includes('bed')) || (r2.type==='bathroom' && r1.type.includes('bed'));
        const isOpen = (r1.type==='living' && r2.type==='dining') || (r2.type==='living' && r1.type==='dining');

        if(isHall || isSuite || isOpen) {
           if(tX && oY > 3) add({x: (r1.x<r2.x?r2.x:r1.x), y: Math.max(r1.y,r2.y)+oY/2-1.5, w:3, vertical:true});
           else if(tY && oX > 3) add({x: Math.max(r1.x,r2.x)+oX/2-1.5, y: (r1.y<r2.y?r2.y:r1.y), w:3, vertical:false});
        }
      });
    });
    if(fIdx===0) {
       const h = rooms.find(r=>r.type==='hallway');
       if(h) add({x: h.x+h.w/2-2, y: h.bottom, w:4, vertical:false, type:'main'});
    }
    allDoors.push(doors);
  });
  blackboard.write('floorDoors', allDoors);
  return true;
};

// --- AGENT 5: FURNITURE (NEW) ---
const FurnitureAgent = async () => {
  LOG('Placing furniture...');
  const floorRegions = blackboard.read('floorRegions');
  const allFurniture = [];

  floorRegions.forEach(rooms => {
    const items = [];
    rooms.forEach(r => {
       const cx = r.x + r.w/2;
       const cy = r.y + r.h/2;

       // 1. BEDROOMS
       if (r.type.includes('bed')) {
          // Double Bed
          items.push({ type: 'bed', x: cx-3, y: r.y+2, w: 6, h: 7 });
          // Nightstands
          items.push({ type: 'rect', x: cx-4.5, y: r.y+2, w: 1.5, h: 1.5 });
          items.push({ type: 'rect', x: cx+3, y: r.y+2, w: 1.5, h: 1.5 });
       }
       // 2. LIVING
       else if (r.type === 'living') {
          // Sofa L-Shape approximation
          items.push({ type: 'rect', x: cx-4, y: cy-2, w: 8, h: 2.5 }); // Sofa Main
          items.push({ type: 'rect', x: cx-4, y: cy+0.5, w: 2.5, h: 4 }); // Sofa L
          items.push({ type: 'rect', x: cx-1, y: cy+1, w: 4, h: 2.5 }); // Coffee Table
       }
       // 3. DINING
       else if (r.type === 'dining') {
          items.push({ type: 'table-dining', x: cx-3, y: cy-4, w: 6, h: 8 });
       }
       // 4. KITCHEN
       else if (r.type === 'kitchen') {
          // L-Counter
          items.push({ type: 'rect', x: r.x, y: r.y, w: r.w, h: 2 }); // Top Counter
          items.push({ type: 'rect', x: r.x, y: r.y, w: 2, h: r.h }); // Side Counter
          // Island
          items.push({ type: 'rect', x: cx-2, y: cy-1, w: 4, h: 2 });
       }
       // 5. BATHROOM
       else if (r.type === 'bathroom') {
          items.push({ type: 'rect', x: r.x, y: r.y, w: r.w, h: 2 }); // Counter/Sink
          items.push({ type: 'circle', x: r.x+2, y: r.y+1, r: 0.8 }); // Sink Basin
          items.push({ type: 'toilet', x: r.right-2.5, y: r.y+3, w: 2, h: 2 });
       }
       // 6. GARAGE
       else if (r.type === 'garage') {
          // No furniture, handled in renderer to skip
       }
    });
    allFurniture.push(items);
  });
  blackboard.write('floorFurniture', allFurniture);
  return true;
};

