import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { ArrowRight, Box, Layers, AlertCircle, Plus, Minus } from 'lucide-react';

const RoomCounter = ({ label, count, onChange, min = 0 }) => (
  <div className="room-counter">
    <span className="text-sm font-medium text-slate-700">{label}</span>
    <div className="flex items-center gap-3">
      <button className="counter-btn" onClick={() => onChange(Math.max(min, count - 1))}>
        <Minus size={14}/>
      </button>
      <span className="w-4 text-center text-sm font-bold">{count}</span>
      <button className="counter-btn" onClick={() => onChange(count + 1)}>
        <Plus size={14}/>
      </button>
    </div>
  </div>
);

export const Wizard = ({ onStart }) => {
  const [step, setStep] = useState(1);
  const [error, setError] = useState(null);

  // Config State
  const [config, setConfig] = useState({
    width: 50, depth: 60, floors: 1,
    rooms: {
      bedroom: 1, bathroom: 1, kitchen: 1, // Required
      living: 1, dining: 0, prayer: 0, library: 0, 
      garage: 0, utility: 0
    }
  });

  const update = (k, v) => setConfig(p => ({ ...p, [k]: v }));

  const updateRoom = (type, val) => {
    setConfig(prev => ({
      ...prev,
      rooms: { ...prev.rooms, [type]: val }
    }));
  };

  const validateAndGenerate = () => {
    if(config.rooms.bedroom < 1 || config.rooms.kitchen < 1 || config.rooms.bathroom < 1) {
      setError("Must have at least 1 Bedroom, 1 Kitchen, and 1 Bathroom.");
      return;
    }
    setError(null);
    onStart(config);
  };

  return (
    <div className="wizard-container">
      <motion.div initial={{y: 20, opacity: 0}} animate={{y: 0, opacity: 1}} className="wizard-card">
        <div className="flex items-center gap-3 mb-2">
           <div className="bg-indigo-100 p-2 rounded-lg">
             <Box className="text-indigo-600" size={24} /> 
           </div>
           <div>
             <h1 className="text-2xl font-bold text-slate-900 leading-none">ShamArchitectSys</h1>
             <span className="text-xs text-indigo-500 font-semibold tracking-wider">INTELLIGENT BLUEPRINT</span>
           </div>
        </div>
        <div className="h-px bg-slate-100 my-4"></div>

        {step === 1 && (
          <div className="space-y-6">
            <h2 className="text-lg font-semibold text-slate-800">1. Site & Structure</h2>
            <div className="grid grid-cols-2 gap-6">
              <div className="input-group">
                <label>Plot Width (ft)</label>
                <input type="number" className="input-field" value={config.width} onChange={e => update('width', +e.target.value)} />
              </div>
              <div className="input-group">
                <label>Plot Depth (ft)</label>
                <input type="number" className="input-field" value={config.depth} onChange={e => update('depth', +e.target.value)} />
              </div>
            </div>

            <div className="input-group">
               <label>Number of Floors</label>
               <div className="grid grid-cols-3 gap-3 mt-1">
                 {[1, 2, 3].map(n => (
                   <button key={n} onClick={() => update('floors', n)}
                     className={`py-3 rounded-lg border font-semibold transition-all ${config.floors === n ? 'bg-indigo-600 text-white border-indigo-600 shadow-md' : 'bg-white text-slate-600 border-slate-200'}`}>
                     {n} Floor{n > 1 ? 's' : ''}
                   </button>
                 ))}
               </div>
            </div>

            <div className="flex justify-end mt-8">
              <button className="btn btn-primary" onClick={() => setStep(2)}>Next Step <ArrowRight size={18}/></button>
            </div>
          </div>
        )}

        {step === 2 && (
          <div className="space-y-6">
            <h2 className="text-lg font-semibold text-slate-800">2. Room Selection</h2>

            <div className="grid grid-cols-2 gap-x-8 gap-y-2">
              <div className="col-span-2 mb-2">
                <span className="text-xs font-bold text-slate-400 uppercase tracking-wider">Essentials (Required)</span>
              </div>
              <RoomCounter label="Bedroom(s)" count={config.rooms.bedroom} min={1} onChange={v => updateRoom('bedroom', v)} />
              <RoomCounter label="Bathroom(s)" count={config.rooms.bathroom} min={1} onChange={v => updateRoom('bathroom', v)} />
              <RoomCounter label="Kitchen" count={config.rooms.kitchen} min={1} onChange={v => updateRoom('kitchen', v)} />

              <div className="col-span-2 mt-4 mb-2">
                <span className="text-xs font-bold text-slate-400 uppercase tracking-wider">Living & Spiritual</span>
              </div>
              <RoomCounter label="Living Room" count={config.rooms.living} onChange={v => updateRoom('living', v)} />
              <RoomCounter label="Dining Room" count={config.rooms.dining} onChange={v => updateRoom('dining', v)} />
              <RoomCounter label="Prayer Room" count={config.rooms.prayer} onChange={v => updateRoom('prayer', v)} />
              <RoomCounter label="Library / Study" count={config.rooms.library} onChange={v => updateRoom('library', v)} />

              <div className="col-span-2 mt-4 mb-2">
                <span className="text-xs font-bold text-slate-400 uppercase tracking-wider">Utility</span>
              </div>
              <RoomCounter label="Garage" count={config.rooms.garage} onChange={v => updateRoom('garage', v)} />
              <RoomCounter label="Utility / Laundry" count={config.rooms.utility} onChange={v => updateRoom('utility', v)} />
            </div>

            {error && (
              <div className="flex items-center gap-2 p-3 bg-red-50 text-red-700 rounded-lg text-sm">
                <AlertCircle size={16}/> {error}
              </div>
            )}

            <div className="flex justify-between mt-8 pt-4 border-t border-slate-100">
              <button className="btn btn-ghost" onClick={() => setStep(1)}>Back</button>
              <button className="btn btn-primary" onClick={validateAndGenerate}>
                Generate Blueprint <Layers size={18}/>
              </button>
            </div>
          </div>
        )}
      </motion.div>
    </div>
  );
};
